import pandas as pd
from simple_salesforce import Salesforce, SalesforceLogin
import json

login = json.load(open('login.json'))[2]

username = login['username']
pw = login['password']
token = login['token']
sandbox = login['sandbox']

session_id, instance = Salesforce(username, pw, security_token=token, sandbox=sandbox)
sf = Salesforce(instance=instance, session_id=session_id)
print(sf)
